  <script src="<?php echo APP_ASSETS ?>js/lib/swiper-bundle.min.js?v=<?php echo APP_VER ?>"></script>
  <script>
    $(function() {
      $("[id^='pg-sld']").each(function() {
        var id = this.id.replace("pg-sld", "");
        const thumbSelector = `#pg-thumb${id} .swiper`;
        const mainSelector = `#pg-sld${id} .swiper`;

        // Thumbnail Swiper
        var thumbSwiper = new Swiper(thumbSelector, {
          loop: true,
          spaceBetween: 12,
          slidesPerView: 'auto',
          // width: '112px',
          freeMode: true,
          watchSlidesProgress: true,
        });

        // Main Swiper
        var mainSwiper = new Swiper(mainSelector, {
          loop: true,
          spaceBetween: 10,
          effect: 'fade',
          fadeEffect: {
            crossFade: true
          },
          navigation: {
            nextEl: `#pg-sld${id} .swiper-button-next`,
            prevEl: `#pg-sld${id} .swiper-button-prev`,
          },
          thumbs: {
            swiper: thumbSwiper,
          },
        });
      });
    });
  </script>
